const UserInfo = async () => {
  const session = await getServerSession(authOptions);

  if (!session) {
    return <p>Вы не вошли в систему</p>;
  }

  return (
    <div>
      <h2>Привет, {session.user?.name}</h2>
      <p>Email: {session.user?.email}</p>
    </div>
  );
};

export default UserInfo;

